package hello;
import java.util.*;
public class Squareroot_near {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner input=new Scanner(System.in);
		int t=input.nextInt();
		while(t-->0)
		{
		int num=input.nextInt();
		System.out.println((int)Math.sqrt(num));
		}		

	}

}
