package codechef;

import java.util.Scanner;

public class count_notebooks {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner input=new Scanner(System.in);
		int t=input.nextInt();
		while(t-->0)
		{
			int x=input.nextInt();
			System.out.println(x*10);
		}
		
		
		

	}

}
